<?php defined('BASEPATH') OR exit('No direct script access allowed');/*** */class Report extends CI_Controller{		function __construct()	{		parent::__construct();        $this->load->helper(array('url','string','form'));        $this->load->model('hr/Mdl_report');        //$this->load->model('admin/patient_model');        $this->load->library(array('session','form_validation'));        $session_array = $this->session->userdata('logged_in_admin');        if(!isset($session_array))        {            redirect('admin/Login');        }	}    function set_menu()    {         $data['header'] = $this->load->view('hr/templates/default_assets', '', true);        $data['sidebar'] = $this->load->view('hr/templates/hr_sidebar', '', true);        $data['footer'] = $this->load->view('hr/templates/hr_footer', '', true);        return $data;    }	function index()	{		$data=$this->set_menu();		$data['timesheet'] = $this->report_model->getreports();			$this->load->view('hr/ts_dashboard', $data);	}	// HR REPORT MODULE	function hr_summary()	{		// not used now		$data=$this->set_menu();		$data['hrsummary'] = $this->report_model->hrsummary();			$this->load->view('hr/rp_summary', $data);	}    function payroll_summary()    { if(has_role('view_payroll_report')){        $data=$this->set_menu();        $data['salary'] = $this->Mdl_report->paysummary();        $this->load->view('hr/rp_paysummary', $data);    }    }	function hr_timeline()	{		$data=$this->set_menu();		$data['hrtimeline'] = $this->report_model->hrtimeline();			$this->load->view('hr/rp_hrtimeline', $data);	}	function stations_list()	{		// not used now		$data=$this->set_menu();		$data['stationslist'] = $this->report_model->stationslist();			$this->load->view('rp_stationslist', $data);	}    function departments_list()    {         if(has_role('view_department_report')){        $data=$this->set_menu();        $data['departmentslist'] = $this->Mdl_report->departmentslist();        $this->load->view('hr/rp_departmentslist', $data);         }    }    function designations_list()    {        $data=$this->set_menu();        $data['designationslist'] = $this->report_model->designationslist();        $this->load->view('hr/rp_designationslist', $data);    }	function employees_list()	{		$data=$this->set_menu();		$data['employeeslist'] = $this->report_model->employeeslist();			$this->load->view('hr/rp_employeeslist', $data);	}	function holidays_calendar()	{		$data=$this->set_menu();		$data['holidayscalendar'] = $this->report_model->holidayscalendar();			$this->load->view('hr_new/rp_hcalendar', $data);	}	function birthdays_calendar()	{ if(has_role('view_birhtday_report')){		$data=$this->set_menu();		$data['birthdayscalendar'] = $this->Mdl_report->birthdayscalendar();		$this->load->view('hr/rp_bcalendar', $data);	}        }	function leaves_calendar()	{		$data=$this->set_menu();		$data['leavescalendar'] = $this->Mdl_report->leavescalendar();		$this->load->view('hr/rp_lcalendar', $data);	}	}?>